'use client';
import React, { useState } from 'react';
import Upload from './upload';
import UploadV from './upload-video';
import { Button, Spin, Image, Modal } from 'antd';
import {
  LoadingOutlined,
  PlayCircleOutlined
} from '@ant-design/icons'
import { useDebounceFn } from 'ahooks';
import apiClient from "@/libs/api";
import "../node_modules/video-react/dist/video-react.css"; 
import { Player } from 'video-react';
import { getFirstImageToVideo } from './get-first-image';

const fallback = 'data:image/png;base64,';

const AddVideo = () => {
  const [myImage, setMy] = useState(); //face
  const [isLoading, setLoading] = useState(false);
  const [preView, setPreView] = useState(); //get 
  const [targetImage, setTarget] = useState(); //original
  const [isOpenModal, setModal] = useState(false);

  const handleSubmit = useDebounceFn(async () => {
    setLoading(true);
    // await apiClient.post('/api', {
    //   todo: 'todos'
    // });
    setTimeout(() => {
      setLoading(false);
      getFirstImageToVideo(targetImage, setPreView);
    }, 3000)
  }, {
    leading: true,
    trailing: false,
  })

  const handleCloseModal = () => {
    setModal(false);
  }

  return (
    <>
      <div className='flex justify-center mt-10'>
        <div className='flex w-80 h-80 items-center justify-center rounded-3xl bg-zinc-50'>
          <Upload image={myImage} set={setMy} title="add face"/>
          <span className='m-4'>&gt;&gt;</span>
          <UploadV image={targetImage} set={(url) => { setTarget(url) }} title='original video'/>
        </div>
        <div 
          className='flex w-80 h-80 ml-12 items-center justify-center rounded-3xl bg-zinc-50 relative bg-contain '
          style={{ backgroundImage: `url(${fallback})` }}
        >
          <div className='absolute bg-slate-800 rounded-lg px-3 py-1 text-white top-3 left-3'>Original</div>
          {
            preView && <img className='max-w-80 max-h-80 rounded-3xl' src={preView} fallback={fallback} />
          }
          {
            preView && <PlayCircleOutlined onClick={() => {setModal(true)}} className='absolute' style={{fontSize: '54px', color: 'white'}}/>
          }
        </div>
      </div>
      <Button 
        className='w-3/5 h-14 text-xl mt-10' 
        type="primary" 
        disabled={ !myImage || !targetImage || isLoading }
        onClick={handleSubmit.run}
      >
          Generate
      </Button>

      <Spin 
        spinning={isLoading}
        tip="Loading..."
        indicator={<LoadingOutlined style={{ fontSize: 48, color: 'white' }}/>} 
        fullscreen={true} 
      >
      </Spin>

      <Modal open={isOpenModal && targetImage} onCancel={handleCloseModal} footer={null} centered destroyOnClose closeIcon={false}>
        <Player
          playsInline
          poster={fallback}
          autoPlay
        >
          <source src={targetImage}></source>
        </Player>
      </Modal>
    </>
  )
}

export default AddVideo;